mapper.select:
select count(*) from p_agent pa where 1=1 and agent_up_code = #{schAgentCode}
AND pa.agent_name like '%${schText}%'
AND pa.id like '%${schText}%'

select agent_code , agent_up_code , id , password , password_str , agent_name , memo , format(point, 0) as point , date_format(reg_date, '%Y-%m-%d') as regDate , ticket , format(ticket, 0) as ticketStr , ticket2 , format(ticket2, 0) as ticketStr2 , ticket3 , format(ticket3, 0) as ticketStr3 , ticket4 , format(ticket4, 0) as ticketStr4 , ticket5 , format(ticket5, 0) as ticketStr5 from p_agent pa where 1=1 and agent_up_code = #{schAgentCode}
AND pu.nick_name like '%${schText}%'
AND pu.phone_num like '%${schText}%'
LIMIT #{paginationInfo.firstRecordIndex}, #{recordsPerPage}

mapper.update:
UPDATE p_agent
SET agent_name = #{agentName}
, memo = #{memo}
, password = #{password}
, password_str = #{passwordStr}
WHERE 1=1
AND agent_code = #{agentCode}

mapper.delete:
DELETE FROM p_agent
WHERE 1=1
AND agent_code = #{agentCode}

mapper.update:
UPDATE p_agent
SET point = point + #{addPoint}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket = ticket + #{addTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket2 = ticket2 + #{addTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket3 = ticket3 + #{addTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket4 = ticket4 + #{addTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket5 = ticket5 + #{addTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET point = point - #{minusPoint}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket = ticket - #{minusTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket2 = ticket2 - #{minusTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket3 = ticket3 - #{minusTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket4 = ticket4 - #{minusTicket}
WHERE 1=1
AND agent_code = #{agentCode}

UPDATE p_agent
SET ticket5 = ticket5 - #{minusTicket}
WHERE 1=1
AND agent_code = #{agentCode}

mapper.select:
SELECT count(*)
FROM p_top_agent_point
WHERE 1=1




			AND agent_code = #{schCond1}



		AND agent_code = #{agentCode}





		AND gbn = 'MINUS'
		AND point = #{schText}


		AND gbn = 'ADD'
		AND point = #{schText}




	and reg_date >= concat(#{schStartDte}, ' 00:00:00')
	and reg_date <= concat(#{schEndDte}, ' 23:59:59')

select pa.agent_name , case when ptap.gbn = 'ADD' then ptap.point else 0 end as addPoint , case when ptap.gbn = 'MINUS' then ptap.point * -1 else 0 end as minusPoint , date_format(ptap.reg_date, '%Y-%m-%d %H:%i:%s') as regDate from p_top_agent_point ptap LEFT JOIN p_agent pa on pa.agent_code = ptap.agent_code where 1=1
AND ptap.agent_code = #{schCond1}
AND ptap.agent_code = #{agentCode}
AND ptap.gbn = 'MINUS' AND ptap.point = #{schText}
AND ptap.gbn = 'ADD' AND ptap.point = #{schText}
and ptap.reg_date >= concat(#{schStartDte}, ' 00:00:00') and ptap.reg_date <= concat(#{schEndDte}, ' 23:59:59')
order by ptap.reg_date desc
LIMIT #{paginationInfo.firstRecordIndex}, #{recordsPerPage}
