mapper.select:
select count(*) from g_info gi where 1=1
AND gi.game_name like '%${schText}%'
AND gi.reg_user_id = #{schCond2}

select seq , game_name , date_format(reg_date, '%Y-%m-%d') as regDate , format(start_stack, 0) as start_stack , format(rebuy_stack, 0) as rebuy_stack , format(rebuy_stack2, 0) as rebuy_stack2 , format(max_entry, 0) as max_entry from g_info gi where 1=1
AND gi.game_name like '%${schText}%'
AND gi.reg_user_id = #{schCond2}
LIMIT #{paginationInfo.firstRecordIndex}, #{recordsPerPage}

mapper.insert:
INSERT INTO g_info
( game_name
, reg_date
, start_stack
, rebuy_stack
, rebuy_stack2
, max_entry
, reg_user_id)
VALUES
( #{gameName}
, NOW()
, #{startStack}
, #{rebuyStack}
, #{rebuyStack2}
, #{maxEntry}
, #{regUserId})

mapper.update:
UPDATE g_info
SET game_name = #{gameName}
, start_stack = #{startStack}
, rebuy_stack = #{rebuyStack}
, rebuy_stack2 = #{rebuyStack2}
, max_entry = #{maxEntry}
WHERE 1=1
AND seq = #{seq}

mapper.delete:
DELETE FROM g_info
WHERE 1=1
AND seq = #{seq}

mapper.insert:
