mapper.update:
UPDATE p_jackpot
SET jackpot_title = #{jackpotTitle}
, use_yn = #{useYn}
WHERE 1=1
AND seq = #{seq}

mapper.delete:
DELETE FROM p_jackpot
WHERE 1=1
AND seq = #{seq}

mapper.update:
UPDATE p_jackpot_noti
SET order_no = #{orderNo}
, content = #{content}
WHERE 1=1
AND seq = #{seq}

mapper.delete:
DELETE FROM p_jackpot_noti
WHERE 1=1
AND seq = #{seq}

mapper.update:
UPDATE p_jackpot
SET jackpot_prize = jackpot_prize + #{addJackpotPrize}
WHERE 1=1
AND seq = #{jackpotSeq}

UPDATE p_jackpot
SET jackpot_prize = jackpot_prize - #{minusJackpotPrize}
WHERE 1=1
AND seq = #{jackpotSeq}

mapper.select:
SELECT pja.seq , pja.jackpot_seq , pja.alert_content , pja.alert_yn , date_format(pja.reg_date, '%Y-%m-%d %H:%i:%s') as regDate , pja.jackpot_prize , pj.jackpot_title , pa.agent_name FROM p_jackpot_alert pja LEFT JOIN p_jackpot pj on pj.seq = pja.jackpot_seq LEFT JOIN p_agent pa on pa.agent_code = pja.reg_agent_code WHERE 1=1
AND reg_agent_code = #{regAgentCode}
AND reg_agent_code = #{schCond1}
and pja.reg_date BETWEEN DATE_FORMAT(#{schStartDte}, '%Y-%m-%d') and DATE_ADD(DATE_FORMAT(#{schEndDte}, '%Y-%m-%d'), INTERVAL 1 day)
ORDER BY pja.reg_date DESC
LIMIT #{paginationInfo.firstRecordIndex}, #{recordsPerPage}

SELECT count(*) as cnt FROM p_jackpot_alert pja LEFT JOIN p_jackpot pj on pj.seq = pja.jackpot_seq LEFT JOIN p_agent pa on pa.agent_code = pja.reg_agent_code WHERE 1=1
AND reg_agent_code = #{regAgentCode}
AND reg_agent_code = #{schCond1}
and pja.reg_date BETWEEN DATE_FORMAT(#{schStartDte}, '%Y-%m-%d') and DATE_ADD(DATE_FORMAT(#{schEndDte}, '%Y-%m-%d'), INTERVAL 1 day)
